import React from "react";
import ScrollArea from 'react-scrollbar';
import 'bootstrap/dist/css/bootstrap.min.css';
import "../style/SingleImage.css";
import {Link} from 'react-router-dom';

import SideNavbar from './SideNavbar';
import TopNavbar from './TopNavbar';
import Footer from './Footer';

export default class SingleImage extends React.Component {
  constructor(props) {
    super(props);

    // The state maintained by this React Component.
    // This component maintains a list of randomly picked paintings
    this.state = {
        source: "",
        title: "",
        technique: "",
        type: "",
        size: "",
        form: "",
        date: "",
        artist: "",
        description: "",
        school: "",
        location: "",
        recImagesDiv:[]
    }

    // Any instance method should be binded here
    this.fetchImages = this.fetchImages.bind(this);
  }

  componentDidMount() {
    this.fetchImages();

  }

  componentDidUpdate(prevProps) {
    if (this.props.match.params.imageID !== prevProps.match.params.imageID) {
      this.fetchImages();
    }
  }

  fetchImages(){
    fetch("http://localhost:8081/image/"+this.props.match.params.imageID,
    {
      method: 'GET' // The type of HTTP request.
    }).then(res => {
      // Convert the response data to a JSON.
      return res.json();
    }, err => {
      // Print the error if there is one.
      console.log(err);
    }).then(imageInfoArr => {
      if (!imageInfoArr) return;
      var imageInfo = imageInfoArr[0];
      this.setState({
        source: imageInfo.IMAGE_SOURCE,
        title: imageInfo.TITLE,
        technique: imageInfo.TECHNIQUE,
        type: imageInfo.TYPE,
        size: imageInfo.SIZES,
        form: imageInfo.TYPE,
        date: imageInfo.CREATED_TIME,
        artist: imageInfo.AUTHOR,
        description: imageInfo.DESCRIPTION,
        school: imageInfo.SCHOOL,
        location: imageInfo.LOCATION
      });
    }, err => {
      // Print the error if there is one.
      console.log(err);
    });

    fetch("http://localhost:8081/recs/"+this.props.match.params.imageID,
    {
      method: 'GET' // The type of HTTP request.
    }).then(res => {
      // Convert the response data to a JSON.
      return res.json();
    }, err => {
      // Print the error if there is one.
      console.log(err);
    }).then(imageList => {
      if (!imageList) return;

      let recImages = imageList.map((imgObj, i) =>
      <div className="rec-img-container"><Link to={"/singleimage/" + imgObj.ID}><img key={i} src={imgObj.image_source} alt=""/></Link></div>
      );
      console.log(recImages);

      this.setState({
        recImagesDiv:recImages
      });

    }, err => {
      // Print the error if there is one.
      console.log(err);
    });

  }

  render() {

    return (
    <div>

      <SideNavbar />


      <div id="main">

        <TopNavbar/>

        <div className="img-info-section">

          <div className="img-section">
            <div className="img-container">
              <img src={this.state.source} alt=""/>
            </div>
          </div>

          <div className="info-section">

            <div>TITLE: {this.state.title}</div>
            <div>SIZE: {this.state.size}</div>
            <div>ARTIST: {this.state.artist}</div>
            <div>CREATED AT: {this.state.date}</div>
            <div>TECHNIQUE: {this.state.technique}</div>
            <div>TYPE: {this.state.type}</div>
            <div>FORM: {this.state.form}</div>
            <div>NOW AT: {this.state.location}</div>
            <div>{this.state.description}</div>

          </div>

        </div>

        <div className="rec-section">
          <h1> Explore more artworks </h1>

          <div className="rec-imgs">
            {this.state.recImagesDiv}
          </div>

        </div>



        <Footer />

      </div>

    </div>

    );
  }

}
